def get_historical_url(
    stock_code: str,
    limit: int,
    start_date: str = "2023-01-01",
    end_date: str = (datetime.date.today()).strftime("%Y-%m-%d"),
):
    return f"https://restv2.fireant.vn/symbols/{stock_code}/historical-quotes?startDate={start_date}&endDate={end_date}&offset=0&limit={limit}"


def get_fireant_dataset(stock_code: str, limit: int, start_date: str, end_date: str):
    url = get_historical_url(
        stock_code=stock_code, limit=limit, start_date=start_date, end_date=end_date
    )
    response = requests.get(url, headers=headers).json()
    print("Total samples:", len(response))
    priceCloses = []
    for daily in reversed(response):
        priceCloses.append(daily["priceClose"])
    dataset = np.asarray(priceCloses, "float32")
    dataset = np.reshape(dataset, (-1, 1))
    return dataset
